/*
 * Get java, Problem 4-2-3
 * extraordinary output using shift operator
 */
public class P4_2_3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int plus = 8;
		int minus = -8;
		int bitset = 1 << 31; // move by MSB
		
		plus |= bitset; // change MSB
		System.out.println(plus);
		
		minus ^= bitset;
		System.out.println(minus);
		/*
		System.out.println(plus << 28); // move by MSB
		System.out.println(minus >>> 1); // change MSB
		*/
		
	}

}
